package com.example.typejam;

import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.fxml.FXMLLoader;
import javafx.scene.Node;
import javafx.scene.Parent;
import javafx.scene.Scene;
import javafx.stage.Stage;

import java.io.IOException;

public class EnterNameController {

    @FXML
    private void onBack(ActionEvent event) throws IOException {
        switchTo(event, "main-menu.fxml");
    }

    @FXML
    private void onNext(ActionEvent event) throws IOException {
        switchTo(event, "select-mode.fxml");
    }

    private void switchTo(ActionEvent event, String fxmlName) throws IOException {
        Parent root = FXMLLoader.load(getClass().getResource(fxmlName));
        Stage stage = (Stage) ((Node) event.getSource()).getScene().getWindow();
        Scene scene = stage.getScene();
        if (scene == null) {
            scene = new Scene(root, 760, 495);
            stage.setScene(scene);
        } else {
            scene.setRoot(root);
        }
    }
}

package com.example.typejam;

import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.fxml.FXMLLoader;
import javafx.scene.Node;
import javafx.scene.Parent;
import javafx.scene.Scene;
import javafx.scene.control.Button;
import javafx.stage.Stage;

import java.io.IOException;

public class EnterNameController {

    @FXML
    private Button back_btn;

    @FXML
    private void onBack(ActionEvent event) {
        try {
            System.out.println("Back button clicked - navigating to main-menu.fxml");
            switchTo(event, "main-menu.fxml");
        } catch (IOException e) {
            e.printStackTrace();
            System.err.println("Error navigating to main menu: " + e.getMessage());
        }
    }

    @FXML
    private void onNext(ActionEvent event) {
        try {
            System.out.println("Next button clicked - navigating to select-mode.fxml");
            switchTo(event, "select-mode.fxml");
        } catch (IOException e) {
            e.printStackTrace();
            System.err.println("Error navigating to select mode: " + e.getMessage());
        }
    }

    private void switchTo(ActionEvent event, String fxmlName) throws IOException {
        FXMLLoader loader = new FXMLLoader(getClass().getResource(fxmlName));
        Parent root = loader.load();
        Stage stage = (Stage) ((Node) event.getSource()).getScene().getWindow();
        Scene scene = new Scene(root, 760, 495);
        stage.setScene(scene);
        stage.show();
    }
}

package com.example.typejam;

import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.fxml.FXMLLoader;
import javafx.scene.Node;
import javafx.scene.Parent;
import javafx.scene.Scene;
import javafx.scene.control.Button;
import javafx.stage.Stage;

import java.io.IOException;

public class MainMenuController {

    @FXML
    private Button play_btn;

    @FXML
    private Button leaderboard_btn;

    @FXML
    private Button about_btn;

    @FXML
    private Button settings_btn;

    @FXML
    private void onPlay(ActionEvent event) throws IOException {
        switchTo(event, "enter-name.fxml");
    }

    @FXML
    private void onLeaderboard(ActionEvent event) throws IOException {
        switchTo(event, "leaderboards.fxml");
    }

    @FXML
    private void onAbout(ActionEvent event) throws IOException {
        switchTo(event, "about-us.fxml");
    }

    @FXML
    private void onSettings(ActionEvent event) throws IOException {
        switchTo(event, "settings.fxml");
    }

    private void switchTo(ActionEvent event, String fxmlName) throws IOException {
        Parent root = FXMLLoader.load(getClass().getResource(fxmlName));
        // Keep the same stage and scene size; just replace the root.
        Stage stage = (Stage) ((Node) event.getSource()).getScene().getWindow();
        Scene scene = stage.getScene();
        if (scene == null) {
            // Fallback: create a new scene if missing (shouldn't happen in normal flow)
            scene = new Scene(root, 760, 495);
            stage.setScene(scene);
        } else {
            scene.setRoot(root);
        }
    }
}

